items.featureInfoTemplate:
{{#generalname}}
  <h2>{{generalname}}</h2>
{{/generalname}}
{{^generalname}}
  <h2>Point of Interest Information</h2>
{{/generalname}}

<img src='https://ev-infrastructure-mapping.s3-ap-southeast-2.amazonaws.com/visual-assets/poi-feature-box-images/{{buildingcomplextype}}{{generalculturaltype}}.png' style='float: left; width: 20%; margin-right: 2%;'>
{{#classsubtype}}<h3>{{classsubtype}}
{{#buildingcomplextype}} - {{buildingcomplextype}}{{/buildingcomplextype}}
{{#generalculturaltype}} - {{generalculturaltype}}{{/generalculturaltype}}
</h3>{{/classsubtype}}
{{#operationalstatus}}<p>{{operationalstatus}}</p>{{/operationalstatus}}
{{#urbanity}}<p>Urbanity: {{urbanity}}</p>{{/urbanity}}


<div style='clear: both;''></div>

<dl class='isf-info'>
{{#placetype}}              <dt>Category:              </dt><dd>{{placetype}}                   </dd>{{/placetype}}
</dl>

{{#population}}<h3>Population</h3>
<dl class='isf-info'>
{{#population}}             <dt>Population:            </dt><dd>{{population}}            </dd>{{/population}}
{{#popsourcedate}}          <dt>Population source date:</dt><dd>{{popsourcedate}}.        </dd>{{/popsourcedate}}
</dl>
{{/population}}

{{#lastupdate}}<h3>Metadata</h3>
<dl class='isf-info'>
{{#featurereliabilitydate}} <dt>Data reliable as at:   </dt><dd>{{featurereliabilitydate}}</dd>{{/featurereliabilitydate}}
{{#lastupdate}}             <dt>Last update            </dt><dd>{{lastupdate}}            </dd>{{/lastupdate}}
</dl>
{{/lastupdate}}

